        //             <div key={event.id}>
        //                 <h2>{event.title}</h2>
        //             </div>
        //         )
        //     })}
    
        // }
        
    }



    render(){

        return (
            <div className="container">
                 {this.renderEvents()}

            </div>
        )
    }
}

export default Events
