import React from 'react'
import { useParams } from 'react-router-dom'

class Events extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            events : [],
            message: ''
        }
    }
    componentDidMount() {
        const artistname = this.props.match.params.artistname;
    }

    fetchEvents = (name) => {
        const searchUrl = `https://rest.bandsintown.com/artists/${name}/events?app_id=codingbootcamp`
        axios.get(searchUrl)
        .then(res => {
                //const resultNotFoundMsg = (res.data==="" || res.data.error==="Not Found") ? "There are no search results. Please try a new search" : ""
                this.setState({
                    events: res.data,
                    //message: resultNotFoundMsg,
                })
        })
        .catch(error => {
            if(error){
                this.setState({
                    message: 'Failed to fetch the data'
                })
            
            }
        })
    }



    render(){

        return (
            <div>
                <h1></h1>

            </div>
        )
    }
}

export default Events
