import React from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'

class Events extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            events : [],
            message: ''
        }
    }
    componentDidMount() {
        const artistname = this.props.match.params.artistname;
        this.fetchEvents(artistname)

    }

    fetchEvents = (name) => {
        const searchUrl = `https://rest.bandsintown.com/artists/${name}/events?app_id=codingbootcamp`
        axios.get(searchUrl)
        .then(res => {
                const resultNotFoundMsg = res.data.length ? "No upcoming events" : ""
                this.setState({
                    events: res.data,
                    message: resultNotFoundMsg,
                })
        })
        .catch(error => {
            if(error){
                this.setState({
                    message: 'Failed to fetch the data'
                })
            
            }
        })
    }


    renderEvents = () => {
        const events  = this.state.events;
        if(events.length){
            {events.map(event => {
                return <div key={event.id}>
                    <h5>{event.title}</h5>
                </div>
            })}
            return (<h1>{events}</h1>)
        }
        // const events  = this.state.events;
        // console.log((Object.keys(events).length && events.length))
        // if((Object.keys(events).length && events.length)){
        //     {events.map(event => {
        //         return(
        //             <div key={event.id}>
        //                 <h2>{event.title}</h2>
        //             </div>
        //         )
        //     })}
    
        // }
        
    }



    render(){

        return (
            <div className="container">
                 {this.renderEvents()}

            </div>
        )
    }
}

export default Events
