import React from 'react'
import '../Search.css'
import axios from 'axios'

class Search extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            query : '',
            results : {},
            loading: false,
            message: ''
        }
        this.cancel = ''
    }

    fetchSearchResults = (query) => {
        //const searchUrl = `http://127.0.0.1:9000/artists/${query}`
        const searchUrl = `https://rest.bandsintown.com/artists/${query}?app_id=codingbootcamp`
        if (this.cancel != ''){
            this.cancel.cancel()
        }
        this.cancel = axios.CancelToken.source();
        axios.get(searchUrl, {
            cancelToken: this.cancel.token
        })
        .then(res => {
                const resultNotFoundMsg = (res.data=="") ? "There are no search results. Please try a new search" : ""
                this.setState({
                    results: res.data,
                    message: resultNotFoundMsg,
                    loading: false
                })
        })
        .catch(error => {
            if(axios.isCancel(error) || error){
                this.setState({
                    loading: false,
                    message: 'Failed to fetch the data'
                })
            
            }
        })
    }

    handleOnInputChange = (event) => {
        const query = event.target.value
        this.setState({query:query, loading:true, message:''}, () => {
            this.fetchSearchResults(query);            
        });
    }

    /* renderSearchResults = () => {
        const { results } = this.state;
        if (Object.keys(results).length && results.length){
            return <div className="results-container">
                {results.map(item => {
                    return (
                        <div key={item.id} className="container">
                            <img src={require('./user.png')}/>
                            <a  href='' className="result-item">
                                <h6 className="name">{item.name}</h6>
                            </a>
                            <a href={item.url}>{item.url}</a>
                        </div>
                    
                    )
                })}
            </div>
        }
    }; */

    renderSearchResults = () => {
        const { results } = this.state;
        if(results != ""){
            return <div className="card" style="width: 18rem;">
            <img src={results.thumb_url}/>
            <a> {results.name}</a>
            <a href="">{results.facebook_page_url}</a>
       </div>
        }
        
    }



    render(){
        const query = this.state.query;
        return (
            <div className="container">
                <h2 className="heading">Search Artists</h2>
                <label className="search-label" htmlFor="search-input">
                    <input
                    type="text"
                    value={query}
                    id="search-input"
                    name="query"
                    placeholder="Search..."
                    onChange={this.handleOnInputChange}
                    />
                    <i className="fa fa-search search-icon" aria-hidden="true"/>
                    </label> 
                    {this.renderSearchResults()}   
            </div>
        )
    }
}

export default Search
