import React from 'react'
import '../Search.css'
import axios from 'axios'

class Search extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            query : '',
            results : {},
            message: ''
        }
        this.cancel = ''
    }

    handleOnInputChange = (event) => {
        const query = event.target.value
        this.setState({query:query, message:''}, () => {
            this.fetchSearchResults(query);            
        });
    }

    fetchSearchResults = (query) => {
        const searchUrl = `https://rest.bandsintown.com/artists/${query}?app_id=codingbootcamp`
        if (this.cancel !== ''){
            this.cancel.cancel()
        }
        this.cancel = axios.CancelToken.source();
        axios.get(searchUrl, {
            cancelToken: this.cancel.token
        })
        .then(res => {
                const resultNotFoundMsg = (res.data==="" || res.data.error==="Not Found") ? "There are no search results. Please try a new search" : ""
                this.setState({
                    results: res.data,
                    message: resultNotFoundMsg,
                })
        })
        .catch(error => {
            if(axios.isCancel(error) || error){
                this.setState({
                    message: 'Failed to fetch the data'
                })
            
            }
        })
    }

   

    /* renderSearchResults = () => {
        const { results } = this.state;
        if (Object.keys(results).length && results.length){
            return <div className="results-container">
                {results.map(item => {
                    return (
                        <div key={item.id} className="container">
                            <img src={require('./user.png')}/>
                            <a  href='' className="result-item">
                                <h6 className="name">{item.name}</h6>
                            </a>
                            <a href={item.url}>{item.url}</a>
                        </div>
                    
                    )
                })}
            </div>
        }
    }; */

    renderSearchResults = () => {
        const results  = this.state.results;
        console.log(results)
        if(Object.keys(results).length || (results.hasOwnProperty('error') && results.error !== "Not Found") ){
            return <div className="card" style={{width: 18 + 'rem'}} >
                <img className="card-img-top" src={results.thumb_url} alt="Card image cap"></img>
                <div className="card-body">
                <h5 className="card-title">{results.name}</h5>
                <p className="card-text" ><a href={results.facebook_page_url}>{results.facebook_page_url}</a></p>
                </div>
       </div>
        }
        
    }



    render(){
        const {query, message} = this.state;
        return (
            <div className="container">
                    <input
                    className="form-control"
                    type="text"
                    value={query}
                    id="search-input"
                    name="query"
                    placeholder="Search..."
                    aria-label="Search"
                    onChange={this.handleOnInputChange}
                    />
                    <br></br>
                    {/* Error message */}
                    {message && <p className="message">{ message }</p>}
                    {this.renderSearchResults()}  
            </div>
        )
    }
}

export default Search
