import React from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'

class Events extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            events : [],
            message: '',
            currentPage: 1,
            todosPerPage: 5
        }
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(event) {
        this.setState({
          currentPage: Number(event.target.id)
        });
      }

    componentDidMount() {
        const artistname = this.props.match.params.artistname;
        this.fetchEvents(artistname)

    }

    fetchEvents = (name) => {
        const searchUrl = `https://rest.bandsintown.com/artists/${name}/events?app_id=codingbootcamp`
        axios.get(searchUrl)
        .then(res => {
                const resultNotFoundMsg = res.data.length ? "No upcoming events" : ""
                this.setState({
                    events: res.data,
                    message: resultNotFoundMsg,
                })
        })
        .catch(error => {
            if(error){
                this.setState({
                    message: 'Failed to fetch the data'
                })
            
            }
        })
    }


    renderEvents = () => {
        const events  = this.state.events;
        const { currentPage, todosPerPage } = this.state;



        if((Object.keys(events).length && events.length)){
            console.log(events)
            const indexOfLastTodo = currentPage * todosPerPage;
            const indexOfFirstTodo = indexOfLastTodo - todosPerPage;
            const currentTodos = events.slice(indexOfFirstTodo, indexOfLastTodo);
    
            return (<div className="row">
                {currentTodos.map(event =>{
                    return <div key={event.id}  className="col-md-4">
                    <div className="card" >
                        <div className="card-body">
                        <h5 className="card-title">{event.title}</h5>
                        <hr></hr>
                        <table className="table table-borderless">
                            <thead>
                                <tr>
                                    <th>Country</th>
                                    <th>City</th>
                                </tr>
                                
                            </thead>
                            <tbody>
                                <tr>
                                    <td>{event.venue.country}</td>
                                    <td>{event.venue.city}</td>
                                </tr>
                            </tbody>
                        </table>

                        <table className="table table-borderless">
                            <thead>
                                <tr>
                                    <th>Venue</th>
                                    <th>Date</th>
                                </tr>
                                
                            </thead>
                            <tbody>
                                <tr>
                                    <td>{event.venue.name}</td>
                                    <td>{event.datetime.split("T").[0]}</td>
                                </tr>
                            </tbody>
                        </table>
                        {/* <h6 className="card-subtitle mb-2 text-muted">City: {event.venue.city}</h6> */}

                        <p className="card-text">{event.description}</p>
                        </div>
                       </div>
                    </div>
                })}
            </div>)


           

        }   //if ends here
 
    }

    renderPageNumbers = () =>{
        const {events, todosPerPage} = this.state
        const pageNumbers = [];
        for (let i = 1; i <= Math.ceil(events.length / todosPerPage); i++) {
          pageNumbers.push(i);
        }
        pageNumbers.map(number => {
            return (
              <li
                key={number}
                id={number}
                onClick={this.handleClick}
              >
                {number}
              </li>
            );
          });
    }



    render(){

        return (
            <div className="container">
                 {this.renderEvents()}
                 <ul id="page-numbers">
                    {this.renderPageNumbers()}
                </ul>

            </div>
        )
    }
}

export default Events
