import React from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'

class Events extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            events : [],
            message: ''
        }
    }
    componentDidMount() {
        const artistname = this.props.match.params.artistname;
        this.fetchEvents(artistname)

    }

    fetchEvents = (name) => {
        const searchUrl = `https://rest.bandsintown.com/artists/${name}/events?app_id=codingbootcamp`
        axios.get(searchUrl)
        .then(res => {
                const resultNotFoundMsg = res.data.length ? "No upcoming events" : ""
                this.setState({
                    events: res.data,
                    message: resultNotFoundMsg,
                })
        })
        .catch(error => {
            if(error){
                this.setState({
                    message: 'Failed to fetch the data'
                })
            
            }
        })
    }


    renderEvents = () => {
        const events  = this.state.events;
        if((Object.keys(events).length && events.length)){
            console.log(events)
            return (<div className="row">
                {events.map(event =>{
                    return <div key={event.id}  className="col-md-4">
                    <div className="card" >
                        <div className="card-body">
                        <h5 className="card-title">{event.title}</h5>
                        <hr></hr>
                        <h6 className="card-subtitle mb-2 text-muted">City {event.venue.city}</h6>
                        <p className="card-text">{event.description}</p>
                        </div>
                       </div>
                    </div>
                })}
            </div>)
        }
 
    }



    render(){

        return (
            <div className="container">
                 {this.renderEvents()}
            </div>
        )
    }
}

export default Events
